import React, { useState, useEffect } from 'react';

const BackButton = ({ onBackClick }) => {
  const [isHovered, setIsHovered] = useState(false);
  const [isClicked, setIsClicked] = useState(false);
  const [loadAnimation, setLoadAnimation] = useState({});
  const [isAnimated, setIsAnimated] = useState(true);

  useEffect(() => {
    const onLoadStyles = {
      left: '15px',
      transition: 'left 0.2s ease-in-out',
    };
    setLoadAnimation(onLoadStyles);
  }, []);

  const handleMouseOver = () => {
    setIsHovered(true);
  };

  const handleMouseOut = () => {
    setIsHovered(false);
  };

  const animationEnd = () => {
    setIsAnimated(false);
    console.log('Button load animation finished.');
  };

  const handleBackButton = () => {
    setIsClicked(true);
    setTimeout(() => {
      setIsClicked(false);
      onBackClick();
    }, 200);
  };

  const defaultStyles = {
    cursor: 'pointer',
    position: 'absolute',
    height: '35px',
    left: isClicked ? '70px' : isHovered ? '10px' : '15px',
    transform: isClicked ? 'rotate(0)' : isHovered ? 'rotate(-2deg)' : 'rotate(0deg)',
    transition: 'left 0.2s ease-in-out, transform 0.2s ease-in-out',
    filter: isClicked ? 'brightness(.95)' : isHovered ? 'brightness(1.05)' : 'brightness(1)',
    zIndex: '9999',
  };

  const styles = {
    ...defaultStyles,
    ...loadAnimation,
  };

  return (
    <div id="back-button-container">
      <img
        id="back-button"
        src="/imgs/Back Bookmark Button.png"
        style={styles}
        alt=""
        onClick={handleBackButton}
        onMouseOver={handleMouseOver}
        onMouseOut={handleMouseOut}
        onAnimationEnd={animationEnd}
      />
    </div>
  );
};

export default BackButton;
