import React, { useState } from 'react';

const AnimatedButton = ({ divID, imgSrc }) => {
  const [isBulging, setIsBulging] = useState(false);

  const handleMouseOver = () => {
    setIsBulging(true);
  };

  const handleMouseOut = () => {
    setIsBulging(false);
  };

  const style = {
    scale: isBulging ? '1.1' : '1',
    transform: isBulging ? 'rotate(-3deg)' : 'rotate(0deg)',
    transition: 'scale 0.1s ease-in-out, transform 0.1s ease-in-out',
    filter: isBulging ? 'brightness(1.05)' : 'brightness(1)',
  };

  return (
    <img
      id={divID}
      onMouseOver={handleMouseOver}
      onMouseOut={handleMouseOut}
      style={style}
      src={imgSrc}
      alt={alt}
    />
  );
};

export default AnimatedButton;
