import React, { useState } from 'react';

const AnimatedButton = ({ divID, imgSrc, additionalStyles }) => {
  const [isBulging, setIsBulging] = useState(false);
  const [isClicked, setIsClicked] = useState(false);

  const handleMouseOver = () => {
    setIsBulging(true);
  };

  const handleMouseOut = () => {
    setIsBulging(false);
  };

  const handleClick = () => {
    setIsClicked(true);

    setTimeout(() => {
      setIsClicked(false);
    }, 150);
  };

  const style = {
    cursor: 'pointer',
    scale: isClicked ? '0.9' : isBulging ? '1.1' : '1',
    transform: isClicked ? 'rotate(0)' : isBulging ? 'rotate(-2deg)' : 'rotate(0deg)',
    transition: 'scale 0.1s ease-in-out, transform 0.1s ease-in-out',
    filter: isClicked ? 'brightness(.95)' : isBulging ? 'brightness(1.05)' : 'brightness(1)',
    zIndex: '9999',
    ...additionalStyles,
  };

  return (
    <img
      id={divID}
      onMouseOver={handleMouseOver}
      onMouseOut={handleMouseOut}
      onClick={handleClick}
      style={style}
      src={imgSrc}
      draggable={'false'}
      alt={''}
    />
  );
};

export default AnimatedButton;
