export function togglePasswordView(buttonElement) {
    const passwordInput = document.getElementById(buttonElement);

    if (passwordInput.type === "password") {
        passwordInput.type = "text";
    } else {
        passwordInput.type = "password";
    }
}

// FEATURE: Encrypt and send login request to server for validation.
export function sendLoginRequest(usernameElement, passwordElement) {
    // Retrieve username and password from UI
    const usernameValue = document.getElementById(usernameElement).value;
    const passwordValue = document.getElementById(passwordElement).value;

    console.log("Attempting to signup.");
}

// FEATURE: Encrypt and send signup request to server for validation.
export function sendSignupRequest(usernameElement, passwordElement) {
    // Retrieve username and password from UI
    const usernameValue = document.getElementById(usernameElement).value;
    const passwordValue = document.getElementById(passwordElement).value;

    console.log("Attempting to signup.");
}
