import * as GameScript from "../GameScript";
import AnimatedButton from './AnimatedButton';

const Menu = () => {

    // Clear textbox
    const clearInput = (inputElement) => {
        inputElement.value = '';
    };


    return (
        <div id="menu-container">
            <input id="name-input" type="text" placeholder="Enter Kitten Name" maxlength="15"/>
            <div id="button-container">
                <AnimatedButton className="menu-button" imgSrc="/imgs/Button Create V2.png"/>
                <AnimatedButton className="menu-button" imgSrc="/imgs/Button Feed V2.png"/>
                <AnimatedButton className="menu-button" imgSrc="/imgs/Button Play V2.png"/>
                <AnimatedButton className="menu-button" imgSrc="/imgs/Button Dress V2.png"/>
            </div>
            <img id="background" src="/imgs/Notebook Menu.png" draggable="false" alt=""/>
        </div>
    );
};
export default Menu;
