import React, { useState } from 'react';

const BackButton = ({ onBackClick }) => {
    const [isHovered, setIsHovered] = useState(false);
    const [isClicked, setIsClicked] = useState(false);

    const handleMouseOver = () => {
        setIsHovered(true);
      };

      const handleMouseOut = () => {
        setIsHovered(false);
      };

      const handleClick = () => {
        setIsClicked(true);
        setTimeout(() => {
          setIsClicked(false);
        }, 150);
      };
    const handleBackButton = () => {
        console.log("BackButton.js");
        onBackClick();
    };

    const style = {
        cursor: 'pointer',
        scale: isClicked ? '0.9' : setIsHovered ? '1.1' : '1',
        transform: isClicked ? 'rotate(0)' : setIsHovered ? 'rotate(-2deg)' : 'rotate(0deg)',
        transition: 'scale 0.1s ease-in-out, transform 0.1s ease-in-out',
        filter: isClicked ? 'brightness(.95)' : setIsHovered ? 'brightness(1.05)' : 'brightness(1)',
        zIndex: '9999',
      };

    return (
        <div id="back-button-container">
            <img id="back-button" src="/imgs/Back Bookmark Button.png" alt="" onClick={handleBackButton}></img>
        </div>
    );
}

export default BackButton
