import React, { useState } from 'react';
import * as GameScript from "../GameScript";
import AnimatedButton from './AnimatedButton';

const Menu = () => {
    const [inputValue, setInputValue] = useState('');

    const handleClearInput = () => {
        // Clear the input value by updating the state
        setInputValue('');
      };

    // Create a new cat object
    const handleCreateButton = () => {
        GameScript.createCat()
        handleClearInput();
    };

    // Create a new cat object
    const handleFeedButton = () => {
        GameScript.feedCat()
    };

    // Create a new cat object
    const handlePlayButton = () => {
        GameScript.playCat()
    };

    // Create a new cat object
    const handleDressButton = () => {
        GameScript.newMessage("/imgs/Cat Icon.png", "Feature coming soon!")
    };

    return (
        <div id="menu-container">
            <div id="cat-status"></div>
            <input id="name-input" type="text" placeholder="Enter Kitten Name" maxlength="15"  onChange={(e) => setInputValue(e.target.value)}/>
            <div id="button-container">
                <AnimatedButton className="menu-button" imgSrc="/imgs/Button Create V2.png" onClick={handleCreateButton}/>
                <AnimatedButton className="menu-button" imgSrc="/imgs/Button Feed V2.png"   onClick={handleFeedButton}/>
                <AnimatedButton className="menu-button" imgSrc="/imgs/Button Play V2.png"   onClick={handlePlayButton}/>
                <AnimatedButton className="menu-button" imgSrc="/imgs/Button Dress V2.png"  onClick={handleDressButton}/>
            </div>
            <img id="menu-background" src="/imgs/Notebook Menu.png" draggable="false" alt=""/>
        </div>
    );
};

export default Menu;
