// ELEMENT: Toggle password input view
export function togglePasswordView(passwordElementName, buttonElementName) {
    const passwordElement = document.getElementById(passwordElementName);
    const buttonElement = document.getElementById(buttonElementName);

    if (passwordElement.type === "password") {
        passwordElement.type = "text";
        buttonElement.src = "/imgs/Hide Button.png";
    } else {
        passwordElement.type = "password";
        buttonElement.src = "/imgs/View Button.png";
    }
}

// FEATURE: Encrypt and send login request to server for validation.
export function sendLoginRequest(usernameElementName, passwordElementName) {
    // Retrieve username and password from UI
    const usernameValue = document.getElementById(usernameElementName).value;
    const passwordValue = document.getElementById(passwordElementName).value;

    console.log("Attempting to signup.");
}

// FEATURE: Encrypt and send signup request to server for validation.
export function sendSignupRequest(usernameElementName, passwordElementName) {
    // Retrieve username and password from UI
    const usernameValue = document.getElementById(usernameElementName).value;
    const passwordValue = document.getElementById(passwordElementName).value;

    console.log("Attempting to signup.");
}
