// GameComponent.js

import React, { useState } from 'react';
import './css/styles-game.css';
import './css/styles.css';

const GameComponent = () => {
  const [showPassword, setShowPassword] = useState(false);

  const togglePassword = () => {
    setShowPassword((prevShowPassword) => !prevShowPassword);
  };

  const generatePassword = () => {
    // Your password generation logic here
  };

    const bulge = (elementId) => {
        const btn = document.getElementById(`${elementId}`);
        btn.style.scale = "1.1";
        btn.style.transform = "rotate(-3deg)";
        btn.style.transition = 'scale .1s ease-in-out, transform .1s ease-in-out';
        btn.style.filter = "brightness(1.05)";
    };

  const debulge = (elementId) => {
    // Your debulge logic here
  };

  const createCat = () => {
    // Your createCat logic here
  };

  const feedCat = () => {
    // Your feedCat logic here
  };

  const playCat = () => {
    // Your playCat logic here
  };

  const newMessage = (imgPath, message) => {
    // Your newMessage logic here
  };

  return (
    <div id="game-window" style={{ marginTop: '100px' }}>
      <div id="game-new-cat"></div>
      <div>
        <div id="cat-container">
          <div id="cat-notifications"></div>
          <img id="game-background" src="/imgs/Yard Window.png" alt="Game Background" />
          <div id="cat-info">
            <div id="cat-info-updates"></div>
            <div id="cat-login">
              <input id="cat-username" type="text" placeholder="Enter Username" maxLength="12" />
              <input
                id="cat-password"
                type={showPassword ? 'text' : 'password'}
                placeholder="Your Password"
                maxLength="12"
                onLoad={generatePassword}
                readOnly
              />
              <img
                id="cat-view-toggle"
                onClick={togglePassword}
                onMouseOver={() => bulge('clb')}
                onMouseOut={() => debulge('clb')}
                src="/imgs/Hide Button.png"
                alt="Toggle Password Visibility"
              />
              <img
                id="clb"
                onClick={togglePassword}
                onMouseOver={() => bulge('clb')}
                onMouseOut={() => debulge('clb')}
                src="/imgs/Button Login V2.png"
                alt="Login Button"
              />
            </div>
            <input id="cat-name" type="text" placeholder="Enter Kitten Name" maxLength="12" />
            <div id="cat-info-buttons">
              <img
                id="cib1"
                onClick={createCat}
                onMouseOver={() => bulge('cib1')}
                onMouseOut={() => debulge('cib1')}
                src="/imgs/Button Create V2.png"
                alt="Create Cat Button"
              />
              <img
                id="cib2"
                onClick={feedCat}
                onMouseOver={() => bulge('cib2')}
                onMouseOut={() => debulge('cib2')}
                src="/imgs/Button Feed V2.png"
                alt="Feed Cat Button"
              />
              <img
                id="cib3"
                onClick={playCat}
                onMouseOver={() => bulge('cib3')}
                onMouseOut={() => debulge('cib3')}
                src="/imgs/Button Play V2.png"
                alt="Play Cat Button"
              />
              <img
                id="cib4"
                onClick={() => newMessage('/imgs/Cat Icon.png', 'Dress Feature coming soon!')}
                onMouseOver={() => bulge('cib4')}
                onMouseOut={() => debulge('cib4')}
                src="/imgs/Button Dress V2.png"
                alt="Dress Cat Button"
              />
            </div>
            <img
              id="cat-info-background"
              src="/imgs/Notebook Menu.png"
              alt="Cat Info Background"
            />
          </div>
        </div>
      </div>
    </div>
  );
};

export default GameComponent;
